/*--edit Checkbox color--*/
input[type=checkbox]:checked:after,
input[type=checkbox]:checked::after,
input[type=radio]:checked:after,
input[type=radio]:checked::after {
  background: #0379f0 !important ;
  border: 1px solid #ced4da !important ;
}
/* --Edit size font and Color -- */
h3, .h3 {
    font-size: 1.29rem;
    color: #0379f0 !important;
}
