REPLACE INTO `hb_email_templates` (`tplname`, `group`, `for`, `language_id`, `subject`, `message`, `altmessage`, `send`, `plain`, `system`, `hidden`, `notify`)
VALUES ('IPAM: Change Notification', 'General', 'Admin', 1, 'IPAM - [{$date}] {$act_name} by [{$who}]', '{if $act == \'newip\' || $act == \'ipdelete\' || $act == \'update\'}\r\nList: {if $list.parent_name}{$list.parent_name} - {/if}{$list.name}\r\n--------------------------------\r\n{/if}\r\n{if $act == \'newip\'}\r\nNew IP addresses were added:\r\n{foreach from=$new item=ip}\r\n{$ip.ipaddress} {if $ip.domains}- {$ip.domains}{/if}\r\n\r\n{/foreach}\r\n{elseif $act == \'ipdelete\'}\r\nIP addresses {$old.ipaddress} was removed:\r\n{$old.ipaddress} {if $old.domains}- {$old.domains}{/if}\r\n{elseif $act == \'update\'}\r\nIP addresses {$old.ipaddress} was updated.\r\nChanges:\r\n{foreach from=$changes item=log}\r\n  {$log.name}: {$log.from} -> {$log.to}\r\n{/foreach}\r\n{elseif $act == \'changename\'}\r\nList name changed from {$old} to {$new}\r\n{elseif $act == \'editlist\'}\r\nList {$old.name} was updated.\r\nChanges:\r\n{foreach from=$changes item=log}\r\n  {$log.name}: {$log.from} -> {$log.to}\r\n{/foreach}\r\n{elseif $act == \'listdelete\'}\r\nList {$old.name} has been deleted\r\n{elseif $act == \'listadd\'}\r\nNew list: {$new.name} has been added\r\n{/if}', '', 1, 1, 1, 0, 0);
